import React, {useState, useEffect} from "react";
// import dp from "../images/dp.jpg";
// import meme from "../images/meme.jpg";
// import edit from "../images/edit.jpg";
import {useHistory, NavLink} from "react-router-dom";

const About = () => {
  const history = useHistory();
  const [userData, setUserData] = useState({});
  const callAboutPage = async () => {
    try {
      const res = await fetch("/about", {
        method: "GET",
        headers: {
          Accept: "application/json",
          "Content-Type": "application/json",
        },
        credentials: "include",
      });
      const data = await res.json();
      console.log(data);
      setUserData(data);

      if (!res.status === 200) {
        const error = new Error(res.error);
        throw error;
      }
    } catch (err) {
      console.log(err);
      history.push("login");
    }
  };
  useEffect(() => {
    callAboutPage();
  }, []);

  return (
    <>
      <div className='container mt-4'>
        <div className='jumbotron'>
          <h1 className='display-4'>{userData.name}</h1>

          <h3>{userData.work}</h3>
          <p>My Links</p>
          <p className='lead'>
            <NavLink to='http://'>Youtube</NavLink>
            <br />
            <NavLink to='http://'>Instagram</NavLink>
            <br />
            <NavLink to='http://'>FaceBook</NavLink>
          </p>
          <hr className='my-4' />
          <p>
            It uses utility classes for typography and
            spacing to space content out within the larger
            container.
          </p>

          <p>User ID : 1216065</p>
          <p>Name : {userData.name}</p>
          <p>email : {userData.email}</p>
          <p>phone : {userData.phone}</p>
          <p>Profession : {userData.work}</p>
          <p className='lead'>
            <NavLink
              className='btn btn-primary btn-lg'
              to='#'
              role='button'
            >
              Learn more
            </NavLink>
          </p>
        </div>
      </div>
    </>
  );
};

export default About;
